import React from 'react'
import { useSelector } from 'react-redux'

const Avatar = ( {src} ) => {
    const { theme } = useSelector(state => state)
  return (
    <div>
        <img src={src} className="avatar"
        style={{filter: `${theme ? 'invert(1)' : 'invert(0)'}`}}/>
    </div>
  )
}

export default Avatar
