import React, { useState } from 'react'
import { useSelector, useDispatch } from 'react-redux'

import {FiMoreHorizontal, FiTrash} from 'react-icons/fi'
import {MdCreate} from 'react-icons/md'

import { GLOBALTYPES } from '../../../redux/actions/globalTypes'

const CardBody = ({post}) => {
    const [readMore, setReadMore] = useState(false)
    const { auth } = useSelector(state => state)
    const dispatch = useDispatch()

    const handleEditPost = (e) =>  {
        dispatch({ type: GLOBALTYPES.STATUS, payload: {...post, onEdit: true }})
    }

    
    return (
        <div className="card_body">
            <div className="card_body-content">
                <h5 className="title">{post.title}</h5>
                <div className="nav-item dropdown option-item">
                    <span id="moreLink" data-bs-toggle="dropdown"> <FiMoreHorizontal /> </span>

                    <div className="dropdown-menu dropdown-menu-start">
                        {
                            auth.user._id === post.userId._id &&
                            <>
                                <div className="dropdown-item" onClick={handleEditPost}>
                                    <span><MdCreate /></span> Edit Post
                                </div>
                                <div className="dropdown-item">
                                    <span><FiTrash /></span> Remove Post
                                </div>
                            </>
                        }
                    </div>
                </div>
                <p>
                    {
                        post.description.length < 180  
                        ? post.description 
                        : readMore ? post.description + ' ' : post.description.slice(0, 180) + '...'
                    }
                    
                    {/* {
                    post.description.length > 180 &&
                    <span className="readMore" onClick={() => setReadMore(!readMore)}>
                        {
                            readMore ? 'Hide content' : 'Read more'
                        }
                    </span>
                    } */}
                </p>
                
                <span>Skill: {post.skillRequired}</span>
                <span>Job Type: {post.jobType}</span>
                <p>{post.salary}$ Avg.Hour</p>
            </div>
        </div>
    )
}

export default CardBody
