import React, { useState, useEffect } from 'react'
import moment from 'moment'
import Carousel from '../Carousel'
import { useNavigate, Link } from 'react-router-dom'

import { useSelector, useDispatch } from 'react-redux'
import { GLOBALTYPES } from '../../redux/actions/globalTypes'
import { deletePost } from '../../redux/actions/postAction'


import {FiMoreHorizontal, FiTrash, FiLink} from 'react-icons/fi'
import {MdCreate} from 'react-icons/md'
import EditPost from './EditPost'
import { BASE_URL } from '../../utils/config'


const PostInfo = ({post}) => {
    const { auth, homePosts } = useSelector(state => state)
    const dispatch = useDispatch()

    const navigate = useNavigate()

    const [onEdit, setOnEdit] = useState(false)

    useEffect(() => {
        if (onEdit) {
          dispatch({ type: GLOBALTYPES.STATUS, payload: {...post, onEdit: true }})
          dispatch({type: GLOBALTYPES.MODAL, payload: true})

        } else {
          dispatch({ type: GLOBALTYPES.STATUS, payload: {...post, onEdit: false }})
          dispatch({type: GLOBALTYPES.MODAL, payload: false})
        }
    }, [onEdit, dispatch, post])

    const handleDeletePost =  () => {
        if(window.confirm("Are you sure want to delete this post?")) {
            dispatch(deletePost({post, auth}))
            return navigate("/lists")
        }
        
    }

    const handleCopyLink = () => {
        navigator.clipboard.writeText(`${BASE_URL}/post/${post._id}`)
    }
    
    return (
        <div className="post_info">
            <div className="card">
                <div className="card-content">
                    <div className="card_header">
                        <h4 style={{fontWeight: "bold"}}  className="title">{post.title}</h4>
                        <div className="nav-item dropdown option-item">
                            <span id="moreLink" data-bs-toggle="dropdown"> <FiMoreHorizontal /> </span>

                            <div className="dropdown-menu dropdown-menu-start">
                                {
                                    auth.user._id === post.userId._id &&
                                    <>
                                        <div className="dropdown-item" onClick={() => setOnEdit(true)}>
                                            <span><MdCreate /></span> Edit Post
                                        </div>
                                        <div className="dropdown-item" onClick={handleDeletePost}>
                                            <span><FiTrash /></span> Remove Post
                                        </div>
                                    </>
                                }
                                <div className="dropdown-item" onClick={handleCopyLink}>
                                    <span><FiLink /></span> Copy Link
                                </div>
                            </div>
                        </div>


                    </div>
                    
                    <p style={{fontWeight: "bold"}}>Salary: <span style={{fontWeight: "normal"}}>{post.salary}$/Hour</span></p>
                    <p style={{fontWeight: "bold"}}>Skill: <span style={{fontWeight: "normal"}}> {post.skillRequired}</span></p>
                    <p style={{fontWeight: "bold"}}>
                       Description: <span style={{fontWeight: "normal"}}>{post.description}</span>
                    </p>
                    <p style={{fontWeight: "bold"}}>Open: <span style={{fontWeight: "normal"}} className="text-danger">{moment(post.createAt).format('YYYY-MM-DD')}</span></p>
                    <p style={{fontWeight: "bold"}}>Deadlines: <span style={{fontWeight: "normal"}} className="text-danger">{moment(post.deadlines).utc().format('MM/DD/YYYY')}</span></p>
                    <p style={{fontWeight: "bold"}} className='employer_info'>About the employer:</p>
                    <div className="show_images">
                        
                        {
                            post.images.length > 0 &&
                            <>
                                <h6 style={{marginTop: "50px"}}>Detail Description</h6>
                                <Carousel images={post.images} id={post._id} />
                            </>
                        }
                    </div>

                    {
                        onEdit && 
                        <EditPost
                        setOnEdit={setOnEdit}
                        />
                    }
                </div>
            </div>
            <div className="similar_card" style={{maxWidth: '700px', margin: '0 5% 0'}}>
                <p style={{fontWeight: "bold"}} >Similar jobs: </p>
            {
                
                homePosts.posts.map(post => (
                    <div style={{maxWidth: '700px', margin: '0 50px 0'}} key={post._id}>
                      <Link to={`/post/${post._id}`} className ="text-dark">
                          <div style={{padding:'5px', boxShadow:'none'}} className='similar_card '><span>{post.title} ({post.salary}$/hour) </span></div>
                        <hr/>
                      </Link>
                    </div>
                  ))
                    
                
            }
            </div>
        </div>
    )
}

export default PostInfo
