import React, {useState, useEffect} from 'react'
import { useSelector, useDispatch} from 'react-redux'
import { useNavigate, Link } from 'react-router-dom'

const Register = () => {
    const { auth } = useSelector(state => state)
    const dispatch = useDispatch()

    const navigate = useNavigate()

    const initialState = { email: '', password: '' }
    const [userData, setUserData] = useState(initialState)
    const { email, password } = userData

    const [typePass, setTypePass] = useState(false)

    useEffect(() => {
        if(auth.token) navigate("/")
    }, [auth.token, navigate])
    

    

    const handleChangeInput = e => {
    const {name, value} = e.target
    setUserData({...userData, [name]: value})
    }

    const handleSubmit = e => {
    e.preventDefault()
    dispatch(login(userData))
    }




    return (
    <div className="auth_page">
        <form onSubmit={handleSubmit}>
        <h3 className="text-uppercase">Looking For Helpers</h3>
        <div className="mb-3">
            <label htmlFor="exampleInputEmail1" className="form-label">Email address</label>
            <input type="email" className="form-control" id="exampleInputEmail1" name="email"
            aria-describedby="emailHelp" onChange={handleChangeInput} value={email} />

            <small className="form-text">
            We'll never share your email with anyone else.
            </small>
        </div>
        <div className="mb-3">
            <label htmlFor="exampleInputPassword1" className="form-label">Password</label>
            <div className="pass">
            <input type={typePass? "text" :"password"} 
            className="form-control" id="exampleInputPassword1" name="password"
            onChange={handleChangeInput} value={password} />
            <small onClick={() => setTypePass(!typePass)}>
                {typePass  ? 'Hide' : 'Show'}
            </small>
            </div>
            
        </div>

        {/* <div className="mb-3 form-check">
            <input type="checkbox" className="form-check-input" id="exampleCheck1" />
            <label className="form-check-label" htmlFor="exampleCheck1">Check me out</label>
        </div> */}

        <button type="submit" className="btn btn-primary"
        disabled={email && password ? false : true}>
            Register
        </button>

        <p className="my-2">You already have an account? <Link to="/login">Login Now</Link></p>
        </form>      
    </div>
    )

}

export default Register
