import React, { useState, useEffect } from 'react'
import { Link, useLocation } from 'react-router-dom'
import { useSelector, useDispatch } from 'react-redux'
import { GLOBALTYPES } from '../../redux/actions/globalTypes'

// Import icons
import 
{  
    FaCompass,
    FaBell
} from 'react-icons/fa'
import {AiFillMessage} from 'react-icons/ai'

import { logout } from '../../redux/actions/authAction'
import Avatar from '../Alert/Avatar'

const Header = () => {
    const navLinks = [
        {label: 'Browse', icon: FaCompass, path: '/'},
        {label: 'Notification', icon: FaBell, path: '/'},
        {label: 'Message', icon: AiFillMessage, path: '/'}
    ]

    const navLinks2 = [
        {label: 'Dashboard', path: '/'},
        {label: 'My Lists', path: '/'},
        {label: 'My Projects', path: '/'},
        {label: 'Inbox', path: '/'},
        {label: 'Feedback', path: '/'}
    ]

    const { auth, theme } = useSelector(state => state)
    const dispatch = useDispatch()
    const { pathName } = useLocation()

    const isActive = (pn) => {
        if(pn === pathName) return 'active'
    }



    return (
        <div>
            <nav className="navbar navbar-expand-lg navbar-light bg-light justify-content-between align-middle">
                <div className="container">
                    <Link to="/">
                        <h1 className="navbar-brand text-uppercase p-0 m-0">LFHelpers</h1>
                    </Link>
                        
                    <div className="menu">
                        <ul className="navbar-nav flex-row">
                            {
                                navLinks.map((link, index) => { 
                                    const Icon = link.icon
                                    return (
                                    <li className={`nav-item px-2 ${isActive(link.path)}`} key={index}>
                                        <Link className="nav-link" to={link.path}>
                                            <Icon /> {link.label}
                                        </Link>
                                        
                                    </li>
                                )})
                                
                            }

                            <li className="nav-item dropdown">
                                <span className="nav-link dropdown-toggle" id="navbarDropdown" 
                                role="button" data-bs-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
                                    <img src={auth.user.avatar} alt="avatar" className="avatar" 
                                    style={{filter: `${theme ? 'invert(1)' : 'invert(0)'}`}}/>
                                </span>
                                
                                <div className="dropdown-menu" aria-labelledby="navbarDropdown">
                                    <Link className="dropdown-item" to={`/profile/${auth.user._id}`}>Profile</Link>

                                    <label htmlFor="theme" className="dropdown-item"
                                    onClick={() => dispatch({
                                        type: GLOBALTYPES.THEME, payload: !theme
                                    })}>
                                        {theme ? 'Light Mode' : 'Dark Mode'}
                                    </label>

                                    <hr className="dropdown-divider"/>
                                    <Link className="dropdown-item" to="/"
                                    onClick={() => dispatch(logout())}>
                                        Log Out
                                    </Link>
                                </div>
                            </li>
                        </ul>
                        
                    </div>
                </div>
            </nav>

            <nav className="navbar navbar-expand-sm navbar-dark bg-dark justify-content-between align-middle">
                <div className="container"> 
                    <ul className="navbar-nav flex-row mb-2">
                        {   
                            navLinks2.map((link, index) => (
                                <li className="nav-item active">
                                    <Link className="nav-link" to={link.path}>{link.label}
                                    
                                    </Link>
                                    
                                </li>
                            ))
                            
                        }
                    </ul>
                </div>
            </nav>
        </div>
    )
}

export default Header
