import React, { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'

const JobStatus = ({job}) => {
    const {auth, jobStatus} = useSelector(state => state)
    const dispatch = useDispatch()

    let statusMapping = {}
    const initialState = {status: job.status}
    const [jobData, setJobData] = useState(initialState)
    
    // const statusMap = [
    //     {name: "accepted", button: "btn-warning"},
    //     {name: "rejected", button: "btn-warning"},
    //     {name: "deleted", button: "btn-warning"},
    //     {name: "cancelled", button: "btn-warning"},
    //     {name: "finished", button: "btn-warning"},
    // ]
    useEffect(() => {
        
        if(job.status === 'applied'){
            statusMapping = {name: "shortlisted", button: "btn-warning"}
        }
    }, [])
    
    const handleChangeStatus = () => {
        setJobData({...jobData, status: 'shortlisted'})
        // console.log(jobData)
    }
    
    return (
        <div className="card_status">
            {statusMapping.map(statusMap)}
            
            <button className="btn btn-warning"
            onClick={handleChangeStatus}>
                Shortlisted
            </button>
            <button className="btn btn-danger">Cancelled</button>
            
        </div>
    )
}

export default JobStatus
