import React, { useState, useEffect } from 'react'

import { FaSearch } from 'react-icons/fa'

import { useSelector, useDispatch } from 'react-redux'
import { getDataAPI } from '../utils/fetchData'
import  { GLOBALTYPES } from '../redux/actions/globalTypes'

import { Link, useParams } from 'react-router-dom'

// import SearchCard from '../Search/SearchCard'
import PostCard from '../components/Posts/PostCard'
import { getAllPosts } from '../redux/actions/postAction'



const Browse = () => {

  const searchBottom = [
    {label: 'Employees', path: '/browse/user'},
    {label: 'Jobs', path: '/browse/jobs'},
    {label: 'Contest', path: '/browse/contest'},
  ]

  const jobTypeList = [
    'Part-time Job', 'Fulltime Job', 'Work From Home', 'Company', 'Other'
  ]

  const hourlyList = [
    {label: '$0 to $199', min: "0", max: "199" },
    {label: '$200 to $499', min: "200", max: "499" },
    {label: '$500 to $999', min: "500", max: "999" },
    {label: '$1000 to $1999', min: "1000", max: "1999" },
    {label: '$2000 to $5000', min: "2000", max: "5000" },
    {label: 'more than $5000', min: "5000" },
  ]

  const [posts, setPosts] = useState([])
  const [category, setCategory] = useState('')
  const [sort, setSort] = useState('')
  const [postSearch, setPostSearch] = useState('')
  const [skillSearch, setSkillSearch] = useState('')

  const [minValue, setMinValue] = useState('')
  const [maxValue, setMaxValue] = useState('')

  const [page, setPage] = useState('')
  const [result, setResult] = useState(0)

  const [checked, setChecked] = useState(false); 

  const { auth, homePosts }  = useSelector(state => state)
  const dispatch = useDispatch()

  useEffect(() =>  {
    dispatch(getAllPosts())
  }, [dispatch])



  useEffect(() => {
    if (postSearch || sort || category || minValue ) {
      getDataAPI(`/allposts?title[regex]=${postSearch}&limit=${15}&sort=${sort}&${category}&salary[gte]=${minValue}&salary[lte]=${maxValue}`)
      .then(res => setPosts(res.data.posts))
      
      .catch(err => {
        dispatch({
          type: GLOBALTYPES.ALERT, 
          payload: {error: err.response.data.msg}})
      })
    } else {
      setPostSearch([])
      setSort('')
      setCategory('')
    }
  }, [postSearch, sort, category, minValue, maxValue, dispatch])

  const handleClose =  ()  => {
    setCategory('')
    setPostSearch('')
    setSkillSearch('')
    setPosts([])
  }

  const handleChange = () => { 
    setChecked(!checked); 
  }; 

  
  return (
    <section className="browse-container container">
      <div className="browse">      
        <div className="breadcrumb"></div>
        <div className="search-bar">

          <div className="search-header">
            <h3>Browse</h3>
          </div>

          <div className="search-body">
            <form className="search_form">
              <input type="text" name="search" value={postSearch} id="search" title="Enter to search"
              onChange={e => setPostSearch(e.target.value)} />
              <div className="search_icon" style={{opacity: postSearch ? 0 : 0.3}}>
                  <span className="material-icons"> <FaSearch /> </span>
                  <span>Enter to Search</span>
              </div>

              <div className="close_search" style={{opacity: postSearch.length === 0 ? 0 : 1}}
              onClick={handleClose}>
                &times;
              </div>
            </form>
          </div>
          
          <div className="search-footer">
            <nav className="navbar navbar-expand-sm">
                  <div className="container"> 
                      <ul className="navbar-nav flex-row mb-0">
                          {   
                              searchBottom.map((link, index) => (
                                  <li className="nav-item" key={index}>
                                      <Link className="nav-link" to={link.path}>
                                          {link.label}
                                      </Link>
                                      
                                  </li>
                              ))
                          }
                      </ul>
                  </div>
              </nav>
          </div>
        </div>
      </div>

      <div className="search-container">
        <div className="search-content-container">
          <div className="search-content">

            <div className="filter-container">
              <div className="filter-card">
                <div className="filter-header" style={{marginBottom: '25px'}}><h4>Filter by:</h4></div>
                <div className="filter-body">
                  <form>
                    <p>Job Type</p>
                    
                    { 
                      jobTypeList.map((jobs, index) => (
                        <div className="form-check" key={index}>
                          <input className="form-check-input" type="checkbox" id="defaultCheck1" 
                          value={`jobType=${jobs}`} onChange={e => handleChange(checked ? setCategory() : setCategory(e.target.value))} />
                          <label className="form-check-label" htmlFor="defaultCheck1" >
                            {jobs}
                          </label>
                        </div>
                      ))
                    }
                    

                    <hr />

                    <p>Hourly rate</p>

                    { 
                      hourlyList.map((hourly, index) => (
                        <div className="form-check" key={index}>
                          <input className="form-check-input" type="checkbox" id="defaultCheck1" 
                          value={``} onChange={e => handleChange(checked ? setCategory() : setCategory(e.target.value))} />
                          <label className="form-check-label" htmlFor="defaultCheck1" >
                            {hourly.label}
                          </label>
                        </div>
                      ))
                    }

                    <hr />
                    
                    <p>Skills</p>
                    <input type="text" name="skillSearch" value={skillSearch} id="skillSearch"
                      onChange={e => setSkillSearch(e.target.value.toLowerCase())} />
                      <div className="search_icon" style={{opacity: skillSearch ? 0 : 0.3}}>
                          <span className="material-icons"> <FaSearch /> </span>
                          <span>Enter to Search</span>
                      </div>

                      <div className="close_search" style={{opacity: skillSearch.length === 0 ? 0 : 1}}
                      onClick={handleClose}>
                        &times;
                    </div>
                    <div className="form-check">
                      <input className="form-check-input" type="checkbox" value="" id="defaultCheck1" />
                      <label className="form-check-label" htmlFor="defaultCheck1">
                        Default checkbox
                      </label>
                    </div>
                  </form>
                  
                </div>
              </div>
            </div>

            <div className="result-container">
                
              {
                  posts.map(post => (
                    <div key={post._id} className="result-card">
                      <Link to={`/post/${post._id}`} className ="text-dark">
                          <PostCard post={post} />
                      </Link>
                      <hr style={{margin: '20px 0'}}></hr>
                    </div>
                      
                  ))
              }

              
            </div>
          </div>
        </div>
      </div>

    </section>
  )
}

export default Browse
