import React from 'react'
import usePagination from '../../hook/usePagination'

const Pagination = React.memo(({totalPages, page, sort}) => {
  const { 
    firstArr, lastArr, isActive, prev, next, jump 
  } = usePagination(totalPages, page, sort)



  return (
    <div className='pagination'>

      <button onClick={prev}>&laquo;</button>
      {
        firstArr.map(num => (
          <button key={num} className={`${isActive(num)}`}
          onClick={() => jump(num)}>
            {num}
          </button>
        ))
      }

      { lastArr.length > 0 && <button>...</button> }
      
      {
        lastArr.map(num => (
          <button key={num} className={`${isActive(num)}`}
          onClick={() => jump(num)}>
            {num}
          </button>
        ))
      }

      <button onClick={next}>&raquo;</button>
    </div>
  )
})

export default Pagination
