import React, { useState, useEffect, useMemo } from 'react'
import { Link, useLocation } from 'react-router-dom'


import { useSelector, useDispatch } from 'react-redux'
import { getDataAPI } from '../utils/fetchData'
import  { GLOBALTYPES } from '../redux/actions/globalTypes'

import { FaSearch } from 'react-icons/fa'

// import SearchCard from '../Search/SearchCard'

import { getAllPosts } from '../redux/actions/postAction'
import Pagination from '../components/Browse/Pagination'

// import Pagination from '../components/Browse/Pagination'

import useQuery from '../hook/useQuery'
import Result from '../components/Browse/Result'
import Sorting from '../components/Browse/Sorting'
import SearchForm from '../components/Browse/SearchForm'


const Browse = () => {

  const searchBottom = [
    {label: 'Employees', path: '/browse/user'},
    {label: 'Jobs', path: '/browse/jobs'},
    {label: 'Contest', path: '/browse/contest'},
  ]

  const jobTypeList = [
    'Part-time Job', 'Fulltime Job', 'Work From Home', 'Company', 'Other'
  ]

  
  const [posts, setPosts] = useState([])
  const [category, setCategory] = useState('')
  
  const [skillSearch, setSkillSearch] = useState('')

  // const [sliderMax, setSliderMax] = useState(1000)
  // const [salaryRange, setSalaryRange] = useState([25, 75])



  const limit = 3


  const [checked, setChecked] = useState(false); 

  const { linkSearch } = useLocation()
 
  // const { auth, homePosts }  = useSelector(state => state)
  // const dispatch = useDispatch()

  // useEffect(() => {
  //   if (postSearch || sort || category || page || limit ) {
  //     getDataAPI(`allposts?title[regex]=${postSearch}&sort=${sort}&${category}&page=${page}&limit=${limit}`)
  //     .then(res => setPosts(res.data.posts) && setCount(res.data.total))
      
  //     .catch(err => {
  //       dispatch({
  //         type: GLOBALTYPES.ALERT, 
  //         payload: {error: err.response.data.msg}})
  //     })

  //   } else {
  //     setPostSearch([])
  //     setSort('')
  //     setCategory('')
  //   }
    
  // }, [postSearch, sort, category, page, limit, dispatch])

  const {page, sort, search} = useMemo(() =>  {
    const page = new URLSearchParams(linkSearch).get('page') || 1
    const sort = new URLSearchParams(linkSearch).get('sort') || '-createdAt'
    const search = new URLSearchParams(linkSearch.get('search')) || ''

    return {
      page: Number(page),
      sort: sort,
      search: search
    }
  }, [linkSearch])


  const {data, loading, error} = useQuery(`/api/allposts?title[regex]=${search}&sort=${sort}&${category}&page=${page}&limit=${limit}`)

  useEffect(() => {
    if(data?.posts) setPosts(data.posts)
  }, [data?.posts])


  const totalPages = useMemo(() => {
    if (!data?.total) return  0
    return Math.ceil(data.total / limit)
  }, [data?.total, limit])

  const handleClose =  ()  => {
    setCategory('')
    setSkillSearch('')
    setPosts([])
  }

  const handleChange = () => { 
    setChecked(!checked); 
  }; 

  
  return (
    <section className="browse-container container">
      <div className="browse">      
        <div className="breadcrumb"></div>
        <div className="search-bar">

          <div className="search-header">
            <h3>Browse</h3>
          </div>

          <div className="search-body" style={{display: "flex"}}>
            <SearchForm />
            <Sorting page={page} sort={sort}/>
          </div>
          
          <div className="search-footer">
            <nav className="navbar navbar-expand-sm">
                  <div className="container"> 
                      <ul className="navbar-nav flex-row mb-0">
                          {   
                              searchBottom.map((link, index) => (
                                  <li className="nav-item" key={index}>
                                      <Link className="nav-link" to={link.path}>
                                          {link.label}
                                      </Link>
                                      
                                  </li>
                              ))
                          }
                      </ul>
                  </div>
              </nav>
          </div>
        </div>
      </div>

      <div className="search-container">
        <div className="search-content-container">
          <div className="search-content">

            <div className="filter-container">
              <div className="filter-card">
                <div className="filter-header" style={{marginBottom: '25px'}}><h4>Filter by:</h4></div>
                <div className="filter-body">
                  <form>
                    <p>Job Type</p>
                    
                    { 
                      jobTypeList.map((jobs, index) => (
                        <div className="form-check" key={index}>
                          <input className="form-check-input" type="checkbox" id="defaultCheck1" 
                          value={`jobType=${jobs}`} onChange={e => handleChange(checked ? setCategory() : setCategory(e.target.value))} />
                          <label className="form-check-label" htmlFor="defaultCheck1" >
                            {jobs}
                          </label>
                        </div>
                      ))
                    }
                    

                    <hr />

                    {/* <p>Hourly rate</p>
                    <label htmlFor="price-min" className="form-label">Price: </label>
                    <input type="range" className="form-range" id="price-min" name="price-min"
                    min={0} max={sliderMax} value={salaryRange}></input>
                    <label htmlFor="price-max" className="form-label">Price: </label>
                    <input type="range" className="form-range" id="price-max" name="price-max"
                    min={0} max={sliderMax}></input>

                    <div className="hourly-form">
                      <div className="form-group ">
                        <input type="number" className="form-control" id="formGroupExampleInput" placeholder="min"
                        />
                      </div>
                      <p> to </p> 
                      <div className="form-group">
                        <input type="number" className="form-control" id="formGroupExampleInput" placeholder="max"
                        />
                      </div>
                    </div>

                    <hr /> */}
                    
                    <p>Skills</p>
                    <input type="text" name="skillSearch" value={skillSearch} id="skillSearch"
                      onChange={e => setSkillSearch(e.target.value.toLowerCase())} />
                      <div className="search_icon" style={{opacity: skillSearch ? 0 : 0.3}}>
                          <span className="material-icons"> <FaSearch /> </span>
                          <span>Enter to Search</span>
                      </div>

                      <div className="close_search" style={{opacity: skillSearch.length === 0 ? 0 : 1}}
                      onClick={handleClose}>
                        &times;
                    </div>
                    <div className="form-check">
                      <input className="form-check-input" type="checkbox" value="" id="defaultCheck1" />
                      <label className="form-check-label" htmlFor="defaultCheck1">
                        Default checkbox
                      </label>
                    </div>
                  </form>
                  
                </div>
              </div>
            </div>

            <div className="result-container">
              {loading && <h2>Loading...</h2>}
              <Result posts={posts} />
              {error && <h2>{error}</h2>}
              <Pagination totalPages={totalPages} page={page} sort={sort}/>

              
            </div>
          </div>
        </div>
      </div>

    </section>
  )
}

export default Browse
