import { GLOBALTYPES } from "./globalTypes";
import { getDataAPI, postDataAPI } from "../../utils/fetchData";

export const JOB_TYPES = {
    LOADING_JOB: 'LOADING_JOB',
    APPLY_JOB: 'APPLY_JOB',
    GET_JOBS: 'GET_JOBS',
    UPDATE_JOBS: 'UPDATE_JOBS',

}

export const applyJob = (data, auth, status) => async(dispatch) => {
    if (data.sop.length < 10) return dispatch({type: GLOBALTYPES.ALERT, payload: {error: "Your reason to apply is too short"}})
    console.log(data)
    console.log(status._id)
    try {
        dispatch({type: JOB_TYPES.LOADING_POST, payload: { loading: true }})
        const res = await postDataAPI(`post/${status._id}/job`, data, auth.token)
        console.log(res)

        dispatch({ type: JOB_TYPES.LOADING_POST, payload: {loading: false} })
        dispatch({ 
            type: JOB_TYPES.CREATE_POST, 
            payload: {...res.data.newJobApply, user: auth.user} 
        })
        dispatch({
            type: GLOBALTYPES.ALERT, 
            payload: ({
                success: res.data.msg
            })
        })
    } catch (err) {
        dispatch({
            type: GLOBALTYPES.ALERT, 
            payload: ({
                error: err.response.data.msg
            })
        })
    }   
}

export const getJobs = (token) => async(dispatch) => {
    try {
        dispatch({type: JOB_TYPES.LOADING_JOB, payload: { loading: true }})
        const res = await getDataAPI('jobs', token)
        dispatch({
            type: JOB_TYPES.GET_JOBS,
            payload: res.data
        })
        dispatch({ type: JOB_TYPES.LOADING_JOB, payload: {loading: false} })
        dispatch({
            type: GLOBALTYPES.ALERT, 
            payload: ({
                success: res.data.msg
            })
        })

    } catch (err) {
        dispatch({
            type: GLOBALTYPES.ALERT, 
            payload: ({
                error: err.response.data.msg
            })
        })
    }
}
