import React from 'react'
import useCustomRoute from '../../hook/useCustomRoute'

const Sorting = ({page, sort}) => {
    const { pushQuery } = useCustomRoute()
    const handleSort = (e) => {
        const { value } = e.target
        pushQuery({page, sort:value})
    }
    return (
        <div className="sorting">
            <span>Sort By: </span>
            <select value={sort} onChange={handleSort}>
                <option value="">Default</option>
                <option value="title">Sort A to Z</option>
                <option value="-title">Sort Z to A</option>
                <option value="salary">Salary: Low-High</option>
                <option value="-salary">Salary: High-Low</option>
            </select>

        </div>
  )
}

export default Sorting
