import moment from 'moment'
import React from 'react'
import Carousel from '../Carousel'

const PostInfo = ({post}) => {
    const Countdown = moment(post.deadlines).diff(moment(), "days")
    if (Countdown === 59) {
        const hours = (moment.duration(Countdown)).hours()
        console.log(hours)
    }
    return (
        <div className="post_info">
            <div className="card">
                <div className="card-content">
                    <h5 className="title">{post.title}</h5>
                    <p>{post.salary}$ Avg.Hour</p>
                    <span>Skill: {post.skillRequired}</span>
                    <p>
                       {post.description}
                    </p>
                    <p>Open: <span className="text-danger">{Countdown}</span> days left</p>
                    <div className="show_images">
                        {
                            post.images.length > 0 && 
                                <Carousel images={post.images} id={post._id} />
                            
                        }
                    </div>
                    
                    
                    
                </div>
            </div>
        </div>
    )
}

export default PostInfo
