import { GLOBALTYPES } from "./globalTypes";
import { postDataAPI } from "../../utils/fetchData";

export const JOB_TYPES = {
    LOADING_JOB: 'LOADING_JOB',
    APPLY_JOB: 'APPLY_JOB',
}

export const applyJob = (data, auth, status) => async(dispatch) => {
    if (data.sop.length < 10) return dispatch({type: GLOBALTYPES.ALERT, payload: {error: "Your reason to apply is too short"}})
    try {
        // dispatch({type: JOB_TYPES.LOADING_POST, payload: { loading: true }})
        const res = postDataAPI(`posts/${status._id}/job`, ...data, auth.token)
        console.log(res)

    } catch (err) {
        dispatch({
            type: GLOBALTYPES.ALERT, 
            payload: ({
                error: err.response.data.msg
            })
        })
    }
    console.log(data)
}
