import React from 'react'


const Pagination = ({page, total, changePage}) => {
  // const newArr = [...Array(totalPages)].map((_, i) => i + 1)
  return(
    <div className="pagination">
      <button className="pagination_prev"
      onClick={() => changePage(page => page - 1)}
      disabled={page === 1}
      >&laquo;</button>
      <button>1</button>
      <button>2</button>
      <button>3</button>
      <button className="pagination_next"
      onClick={() => changePage(page => page + 1)}
      disabled={page === total}
      >&raquo;</button>
    </div>
  )

}


export default Pagination
