import React, {useRef, useState} from 'react'

import { FaSearch } from 'react-icons/fa'
import useCustomRoute from '../../hook/useCustomRoute'

const SearchForm = ({page, sort, search, filter}) => {
    const [postSearch, setPostSearch] = useState("")
    const inputRef = useRef()

    const { pushQuery } = useCustomRoute()

    const handlePostsSearch = (e) => {
        e.preventDefault()
        const value = inputRef.current.value;
        if(!value.trim()) return;
        pushQuery({page, sort, search: value})
    }

    return (
        <form className="search_form" style={{width: "100%"}} onSubmit={handlePostsSearch}>  
            <input type="text" name="search" id="search" title="Enter to search"
            ref={inputRef} />
            <div className="search_icon" style={{opacity: inputRef ? 0 : 0.3}}>
                <span className="material-icons"> <FaSearch /> </span>
                <span>Enter to Search</span>
            </div>

            <div className="close_search" style={{opacity: inputRef.length === 0 ? 0 : 1}}
            onClick={() => setPostSearch('')}>
                &times;
            </div>
            <button>Search</button>
        </form>

  )
}

export default SearchForm
